CREATE SCHEMA taxi;

CREATE EXTERNAL DATA SOURCE [nyctaxi] WITH (LOCATION = 'abfss://yellowtaxi@datalakeytaletu.dfs.core.windows.net/nyc/raw/', CREDENTIAL = [SynapseIdentity]);
GO

CREATE EXTERNAL TABLE taxi.rate_2010_agg
	WITH (
	LOCATION = '2010/',
	DATA_SOURCE = [nyctaxi],
	FILE_FORMAT = [parquet]
	) AS
SELECT
    rateCodeId
    ,SUM(CAST(passengerCount AS BIGINT)) AS PassengerCount
FROM
    OPENROWSET(
        BULK 'https://datalakeytaletu.dfs.core.windows.net/yellowtaxi/nyc/delta/puYear=2010/**',
        FORMAT = 'PARQUET'
    ) AS [result]
GROUP BY rateCodeId;

